import {$$, addClass, css, hasTouch, on, ready, removeClass, toMs, within} from 'uikit-util';

export default function (UIkit) {

    ready(() => {

        let scroll = 0;
        let started = 0;

        on(window, 'load resize', e => UIkit.update(null, e));
        on(window, 'scroll', e => {
            e.dir = scroll <= window.scrollY ? 'down' : 'up';
            e.scrollY = scroll = window.scrollY;
            UIkit.update(null, e);
        }, {capture: true, passive: false});

        on(document, 'animationstart', ({target}) => {
            if ((css(target, 'animationName') || '').match(/^uk-.*(left|right)/)) {

                started++;
                css(document.body, 'overflowX', 'hidden');
                setTimeout(() => {
                    if (!--started) {
                        css(document.body, 'overflowX', '');
                    }
                }, toMs(css(target, 'animationDuration')) + 100);
            }
        }, true);

        if (!hasTouch) {
            return;
        }

        const cls = 'uk-hover';

        on(document, 'tap', ({target}) =>
            $$(`.${cls}`).forEach(el =>
                !within(target, el) && removeClass(el, cls)
            )
        );

        Object.defineProperty(UIkit, 'hoverSelector', {

            set(selector) {
                on(document, 'tap', selector, ({current}) => addClass(current, cls));
            }

        });

        UIkit.hoverSelector = '.uk-animation-toggle, .uk-transition-toggle, [uk-hover]';

    });

}

cover.script:
import Video from './video';
import Class from '../mixin/class';
import {css, Dimensions, isVisible} from 'uikit-util';

export default {

    mixins: [Class, Video],

    props: {
        width: Number,
        height: Number
    },

    data: {
        automute: true
    },

    update: {

        write() {

            const el = this.$el;

            if (!isVisible(el)) {
                return;
            }

            const {offsetHeight: height, offsetWidth: width} = el.parentNode;

            css(
                css(el, {width: '', height: ''}),
                Dimensions.cover(
                    {
                        width: this.width || el.clientWidth,
                        height: this.height || el.clientHeight
                    },
                    {
                        width: width + (width % 2 ? 1 : 0),
                        height: height + (height % 2 ? 1 : 0)
                    }
                )
            );

        },

        events: ['load', 'resize']

    },

    events: {

        loadedmetadata() {
            this.$emit();
        }

    }

};

