                .start([el, dropbar], {height: newHeight}, this.duration)
                .catch(noop)
                .then(() => css(el, {height: '', overflow: ''}));
        },

        getDropdown(el) {
            return this.$getComponent(el, 'drop') || this.$getComponent(el, 'dropdown');
        }

    }

};

