                data.active = false;

                this.targets.every((el, i) => {

                    const {top} = offset(el);
                    const last = i + 1 === this.targets.length;

                    if (!this.overflow && (i === 0 && top > scroll || last && top + el.offsetTop < scroll)) {
                        return false;
                    }

                    if (!last && offset(this.targets[i + 1]).top <= scroll) {
                        return true;
                    }

                    if (scroll >= max) {
                        for (let j = this.targets.length - 1; j > i; j--) {
                            if (isInView(this.targets[j])) {
                                el = this.targets[j];
                                break;
                            }
                        }
                    }

                    return !(data.active = $(filter(this.links, `[href="#${el.id}"]`)));

                });

            },

            write({active}) {

                this.links.forEach(el => el.blur());
                removeClass(this.elements, this.cls);

                if (active) {
                    trigger(this.$el, 'active', [active, addClass(this.closest ? closest(active, this.closest) : active, this.cls)]);
                }

            },

            events: ['scroll', 'load', 'resize']

        }

    ]

};

scrollspy.script:
import {$$, addClass, css, data, filter, isInView, removeClass, toggleClass, trigger} from 'uikit-util';

export default {

    args: 'cls',

    props: {
        cls: 'list',
        target: String,
        hidden: Boolean,
        offsetTop: Number,
        offsetLeft: Number,
        repeat: Boolean,
        delay: Number
    },

    data: () => ({
        cls: [],
        target: false,
        hidden: true,
        offsetTop: 0,
        offsetLeft: 0,
        repeat: false,
        delay: 0,
        inViewClass: 'uk-scrollspy-inview'
    }),

    computed: {

        elements({target}, $el) {
            return target ? $$(target, $el) : [$el];
        }

    },

    update: [

        {

            write() {
                if (this.hidden) {
                    css(filter(this.elements, `:not(.${this.inViewClass})`), 'visibility', 'hidden');
                }
            }

        },

        {

            read(els) {

                // Let child components be applied at least once first
                if ((els.delay = !els.delay)) {
                    this.$emit();
                    return;
                }

                this.elements.forEach((el, i) => {

                    let elData = els[i];

                    if (!elData || elData.el !== el) {
                        const cls = data(el, 'uk-scrollspy-class');
                        elData = {el, toggles: cls && cls.split(',') || this.cls};
                    }

                    elData.show = isInView(el, this.offsetTop, this.offsetLeft);
                    els[i] = elData;

                });
            },

            write(els) {

                let index = this.elements.length === 1 ? 1 : 0;

                this.elements.forEach((el, i) => {

                    const elData = els[i];
                    const cls = elData.toggles[i] || elData.toggles[0];

                    if (elData.show && !elData.inview && !elData.timer) {

                        const show = () => {
                            css(el, 'visibility', '');
                            addClass(el, this.inViewClass);
                            toggleClass(el, cls);

                            trigger(el, 'inview');

                            this.$update(el);

                            elData.inview = true;
                            delete elData.timer;
                        };

                        if (this.delay && index) {
                            elData.timer = setTimeout(show, this.delay * index);
                        } else {
                            show();
                        }

                        index++;

                    } else if (!elData.show && elData.inview && this.repeat) {

                        if (elData.timer) {
                            clearTimeout(elData.timer);
                            delete elData.timer;
                        }

                        css(el, 'visibility', this.hidden ? 'hidden' : '');
                        removeClass(el, this.inViewClass);
                        toggleClass(el, cls);

                        trigger(el, 'outview');

                        this.$update(el);

                        elData.inview = false;

                    }


                });

            },

            events: ['scroll', 'load', 'resize']

        }

    ]

};

sticky.script:
import Class from '../mixin/class';
import {$, addClass, after, Animation, assign, attr, css, fastdom, hasClass, height, isNumeric, isString, isVisible, noop, offset, query, remove, removeClass, replaceClass, toFloat, toggleClass, trigger, within} from 'uikit-util';

export default {

    mixins: [Class],

    attrs: true,

    props: {
        top: null,
        bottom: Boolean,
        offset: Number,
        animation: String,
        clsActive: String,
        clsInactive: String,
        clsFixed: String,
        clsBelow: String,
        selTarget: String,
        widthElement: Boolean,
        showOnUp: Boolean,
        media: 'media',
        targetOffset: Number
    },

    data: {
        top: 0,
        bottom: false,
        offset: 0,
        animation: '',
        clsActive: 'uk-active',
        clsInactive: '',
        clsFixed: 'uk-sticky-fixed',
        clsBelow: 'uk-sticky-below',
        selTarget: '',
        widthElement: false,
        showOnUp: false,
        media: false,
        targetOffset: false
    },

    computed: {

        selTarget({selTarget}, $el) {
            return selTarget && $(selTarget, $el) || $el;
        },

        widthElement({widthElement}, $el) {
            return query(widthElement, $el) || this.placeholder;
        }

    },

    connected() {

        this.placeholder = $('<div class="uk-sticky-placeholder"></div>');

        if (!this.isActive) {
            this.hide();
        }
    },

    disconnected() {

        if (this.isActive) {
            this.isActive = false;
            this.hide();
            removeClass(this.selTarget, this.clsInactive);
        }

        remove(this.placeholder);
        this.placeholder = null;
        this.widthElement = null;
    },

    events: [

        {
            name: 'active',

            self: true,

            handler() {
                replaceClass(this.selTarget, this.clsInactive, this.clsActive);
            }

        },

        {
            name: 'inactive',

            self: true,

            handler() {
                replaceClass(this.selTarget, this.clsActive, this.clsInactive);
            }

        },

        {

            name: 'load hashchange popstate',

            el: window,

            handler() {

                if (!(this.targetOffset !== false && location.hash && window.scrollY > 0)) {
                    return;
                }

                const target = $(location.hash);

                if (target) {
                    fastdom.read(() => {

                        const {top} = offset(target);
                        const elTop = offset(this.$el).top;
                        const elHeight = this.$el.offsetHeight;

                        if (this.isActive && elTop + elHeight >= top && elTop <= top + target.offsetHeight) {
                            window.scroll(0, top - elHeight - (isNumeric(this.targetOffset) ? this.targetOffset : 0) - this.offset);
                        }

                    });
                }

            }

        }

    ],

    update: [

        {

            write() {

                const {placeholder} = this;
                const outerHeight = (this.isActive ? placeholder : this.$el).offsetHeight;

                css(placeholder, assign(
                    {height: css(this.$el, 'position') !== 'absolute' ? outerHeight : ''},
                    css(this.$el, ['marginTop', 'marginBottom', 'marginLeft', 'marginRight'])
                ));

                if (!within(placeholder, document)) {
                    after(this.$el, placeholder);
                    attr(placeholder, 'hidden', '');
                }

                this.topOffset = offset(this.isActive ? placeholder : this.$el).top;
                this.bottomOffset = this.topOffset + outerHeight;

                const bottom = parseProp('bottom', this);

                this.top = Math.max(toFloat(parseProp('top', this)), this.topOffset) - this.offset;
                this.bottom = bottom && bottom - outerHeight;
                this.inactive = this.media && !window.matchMedia(this.media).matches;

                if (this.isActive) {
                    this.update();
                }
            },

            events: ['load', 'resize']

        },

        {

            read(_, {scrollY = window.scrollY}) {

                this.width = (isVisible(this.widthElement) ? this.widthElement : this.$el).offsetWidth;

                return {
                    scroll: this.scroll = scrollY,
                    visible: isVisible(this.$el)
                };
            },

            write({visible, scroll}, {dir} = {}) {

                if (scroll < 0 || !visible || this.disabled || this.showOnUp && !dir) {
                    return;
                }

                if (this.inactive
                    || scroll < this.top
                    || this.showOnUp && (scroll <= this.top || dir === 'down' || dir === 'up' && !this.isActive && scroll <= this.bottomOffset)
                ) {

                    if (!this.isActive) {
                        return;
                    }

                    this.isActive = false;

                    if (this.animation && scroll > this.topOffset) {
                        Animation.cancel(this.$el);
                        Animation.out(this.$el, this.animation).then(() => this.hide(), noop);
                    } else {
                        this.hide();
                    }

                } else if (this.isActive) {

                    this.update();

                } else if (this.animation) {

                    Animation.cancel(this.$el);
                    this.show();
                    Animation.in(this.$el, this.animation).catch(noop);

                } else {
                    this.show();
                }

            },

            events: ['scroll']

        },

    ],

    methods: {

        show() {

            this.isActive = true;
            this.update();
            attr(this.placeholder, 'hidden', null);

        },

        hide() {

            if (!this.isActive || hasClass(this.selTarget, this.clsActive)) {
                trigger(this.$el, 'inactive');
            }

            removeClass(this.$el, this.clsFixed, this.clsBelow);
            css(this.$el, {position: '', top: '', width: ''});
            attr(this.placeholder, 'hidden', '');

        },

        update() {

            const active = this.top !== 0 || this.scroll > this.top;
            let top = Math.max(0, this.offset);

            if (this.bottom && this.scroll > this.bottom - this.offset) {
                top = this.bottom - this.scroll;
            }

            css(this.$el, {
                position: 'fixed',
                top: `${top}px`,
                width: this.width
            });

            if (hasClass(this.selTarget, this.clsActive)) {

                if (!active) {
                    trigger(this.$el, 'inactive');
                }

            } else if (active) {
                trigger(this.$el, 'active');
            }

            toggleClass(this.$el, this.clsBelow, this.scroll > this.bottomOffset);
            addClass(this.$el, this.clsFixed);

        }

    }

};

function parseProp(prop, {$props, $el, [`${prop}Offset`]: propOffset}) {

    const value = $props[prop];

    if (!value) {
        return;
    }

    if (isNumeric(value)) {

        return propOffset + toFloat(value);

    } else if (isString(value) && value.match(/^-?\d+vh$/)) {

        return height(window) * toFloat(value) / 100;

    } else {

        const el = value === true ? $el.parentNode : query(value, $el);

        if (el) {
            return offset(el).top + el.offsetHeight;
        }

    }
}

svg.script:
import {$, after, ajax, append, attr, includes, isString, isVoidElement, noop, Promise, remove, removeAttr, startsWith} from 'uikit-util';

const svgs = {};

export default {

    attrs: true,

    props: {
        id: String,
        icon: String,
        src: String,
        style: String,
        width: Number,
        height: Number,
        ratio: Number,
        'class': String
    },

    data: {
        ratio: 1,
        id: false,
        exclude: ['ratio', 'src', 'icon'],
        'class': ''
    },

    connected() {

        this.class += ' uk-svg';

        if (!this.icon && includes(this.src, '#')) {

            const parts = this.src.split('#');

            if (parts.length > 1) {
                [this.src, this.icon] = parts;
            }
        }

        this.svg = this.getSvg().then(svg => {

            let el;

            if (isString(svg)) {

                if (this.icon && includes(svg, '<symbol')) {
                    svg = parseSymbols(svg, this.icon) || svg;
                }

                el = $(svg.substr(svg.indexOf('<svg')));

            } else {
                el = svg.cloneNode(true);
            }

            if (!el) {
                return Promise.reject('SVG not found.');
            }

            let dimensions = attr(el, 'viewBox');

            if (dimensions) {
                dimensions = dimensions.split(' ');
                this.width = this.$props.width || dimensions[2];
                this.height = this.$props.height || dimensions[3];
            }

            this.width *= this.ratio;
            this.height *= this.ratio;

            for (const prop in this.$options.props) {
                if (this[prop] && !includes(this.exclude, prop)) {
                    attr(el, prop, this[prop]);
                }
            }

            if (!this.id) {
                removeAttr(el, 'id');
            }

            if (this.width && !this.height) {
                removeAttr(el, 'height');
            }

            if (this.height && !this.width) {
                removeAttr(el, 'width');
            }

            const root = this.$el;
            if (isVoidElement(root) || root.tagName === 'CANVAS') {

                attr(root, {hidden: true, id: null});

                const next = root.nextElementSibling;
                if (next && el.isEqualNode(next)) {
                    el = next;
                } else {
                    after(root, el);
                }

            } else {

                const last = root.lastElementChild;
                if (last && el.isEqualNode(last)) {
                    el = last;
                } else {
                    append(root, el);
                }

            }

            this.svgEl = el;

            return el;

        }, noop);

    },

    disconnected() {

        if (isVoidElement(this.$el)) {
            attr(this.$el, {hidden: null, id: this.id || null});
        }

        if (this.svg) {
            this.svg.then(svg => (!this._connected || svg !== this.svgEl) && remove(svg), noop);
        }

        this.svg = this.svgEl = null;

    },

    methods: {

        getSvg() {

            if (!this.src) {
                return Promise.reject();
            }

            if (svgs[this.src]) {
                return svgs[this.src];
            }

            svgs[this.src] = new Promise((resolve, reject) => {

                if (startsWith(this.src, 'data:')) {
                    resolve(decodeURIComponent(this.src.split(',')[1]));
                } else {

                    ajax(this.src).then(
                        xhr => resolve(xhr.response),
                        () => reject('SVG not found.')
                    );

                }

            });

            return svgs[this.src];

        }

    }

};

const symbolRe = /<symbol(.*?id=(['"])(.*?)\2[^]*?<\/)symbol>/g;
const symbols = {};

function parseSymbols(svg, icon) {

    if (!symbols[svg]) {

        symbols[svg] = {};

        let match;
        while ((match = symbolRe.exec(svg))) {
            symbols[svg][match[3]] = `<svg xmlns="http://www.w3.org/2000/svg"${match[1]}svg>`;
        }

        symbolRe.lastIndex = 0;

    }

    return symbols[svg][icon];
}

switcher.script:
import Togglable from '../mixin/togglable';
import {$$, addClass, attr, data, filter, getIndex, hasClass, index, isTouch, matches, queryAll, removeClass} from 'uikit-util';

export default {

    mixins: [Togglable],

    args: 'connect',

    props: {
        connect: String,
        toggle: String,
        active: Number,
        swiping: Boolean
    },

    data: {
        connect: '~.uk-switcher',
        toggle: '> *',
        active: 0,
        swiping: true,
        cls: 'uk-active',
        clsContainer: 'uk-switcher',
        attrItem: 'uk-switcher-item',
        queued: true
    },

    computed: {

        connects({connect}, $el) {
            return queryAll(connect, $el);
        },

        toggles({toggle}, $el) {
            return $$(toggle, $el);
        }

    },

    events: [

        {

            name: 'click',

            delegate() {
                return `${this.toggle}:not(.uk-disabled)`;
            },

            handler(e) {
                e.preventDefault();
                this.show(e.current);
            }

        },

        {
            name: 'click',

            el() {
                return this.connects;
            },

            delegate() {
                return `[${this.attrItem}],[data-${this.attrItem}]`;
            },

            handler(e) {
                e.preventDefault();
                this.show(data(e.current, this.attrItem));
            }
        },

        {
            name: 'swipeRight swipeLeft',

            filter() {
                return this.swiping;
            },

            el() {
                return this.connects;
            },

            handler(e) {
                if (!isTouch(e)) {
                    return;
                }

                e.preventDefault();
                if (!window.getSelection().toString()) {
                    this.show(e.type === 'swipeLeft' ? 'next' : 'previous');
                }
            }
        }

    ],

    update() {

        this.connects.forEach(list => this.updateAria(list.children));
        this.show(filter(this.toggles, `.${this.cls}`)[0] || this.toggles[this.active] || this.toggles[0]);

    },

    methods: {

        show(item) {

            const {length} = this.toggles;
            const prev = !!this.connects.length && index(filter(this.connects[0].children, `.${this.cls}`)[0]);
            const hasPrev = prev >= 0;
            const dir = item === 'previous' ? -1 : 1;

            let toggle, next = getIndex(item, this.toggles, prev);

            for (let i = 0; i < length; i++, next = (next + dir + length) % length) {
                if (!matches(this.toggles[next], '.uk-disabled, [disabled]')) {
                    toggle = this.toggles[next];
                    break;
                }
            }

            if (!toggle || prev >= 0 && hasClass(toggle, this.cls) || prev === next) {
                return;
            }

            removeClass(this.toggles, this.cls);
            attr(this.toggles, 'aria-expanded', false);
            addClass(toggle, this.cls);
            attr(toggle, 'aria-expanded', true);

            this.connects.forEach(list => {
                if (!hasPrev) {
                    this.toggleNow(list.children[next]);
                } else {
                    this.toggleElement([list.children[prev], list.children[next]]);
                }
            });

        }

    }

};

tab.script:
import Switcher from './switcher';
import Class from '../mixin/class';
import {hasClass} from 'uikit-util';

export default {

    mixins: [Class],

    extends: Switcher,

    props: {
        media: 'media'
    },

    data: {
        media: 960,
        attrItem: 'uk-tab-item'
    },

    connected() {

        const cls = hasClass(this.$el, 'uk-tab-left')
            ? 'uk-tab-left'
            : hasClass(this.$el, 'uk-tab-right')
                ? 'uk-tab-right'
                : false;

        if (cls) {
            this.$create('toggle', this.$el, {cls, mode: 'media', media: this.media});
        }
    }

};

toggle.script:
import Togglable from '../mixin/togglable';
import {closest, hasTouch, includes, isTouch, isVisible, matches, once, pointerEnter, pointerLeave, queryAll, trigger} from 'uikit-util';

export default {

    mixins: [Togglable],

    args: 'target',

    props: {
        href: String,
        target: null,
        mode: 'list',
        media: 'media'
    },

    data: {
        href: false,
        target: false,
        mode: 'click',
        queued: true,
        media: false
    },

    computed: {

        target({href, target}, $el) {
            target = queryAll(target || href, $el);
            return target.length && target || [$el];
        }

    },

    events: [

        {

            name: `${pointerEnter} ${pointerLeave}`,

            filter() {
                return includes(this.mode, 'hover');
            },

            handler(e) {
                if (!isTouch(e)) {
                    this.toggle(`toggle${e.type === pointerEnter ? 'show' : 'hide'}`);
                }
            }

        },

        {

            name: 'click',

            filter() {
                return includes(this.mode, 'click') || hasTouch;
            },

            handler(e) {

                if (!isTouch(e) && !includes(this.mode, 'click')) {
                    return;
                }

                // TODO better isToggled handling
                let link;
                if (closest(e.target, 'a[href="#"], button')
                    || (link = closest(e.target, 'a[href]')) && (
                        this.cls
                        || !isVisible(this.target)
                        || link.hash && matches(this.target, link.hash)
                    )
                ) {
                    once(document, 'click', e => e.preventDefault());
                }

                this.toggle();
            }

        }
    ],

    update: {

        write() {

            if (!includes(this.mode, 'media') || !this.media) {
                return;
            }

            const toggled = this.isToggled(this.target);
            if (window.matchMedia(this.media).matches ? !toggled : toggled) {
                this.toggle();
            }

        },

        events: ['load', 'resize']

    },

    methods: {

        toggle(type) {
            if (trigger(this.target, type || 'toggle', [this])) {
                this.toggleElement(this.target);
            }
        }

    }

};

video.script:
import {css, hasAttr, isInView, isVisible, Player} from 'uikit-util';

export default {

    args: 'autoplay',

    props: {
        automute: Boolean,
        autoplay: Boolean,
    },

    data: {
        automute: false,
        autoplay: true
    },

    computed: {

        inView({autoplay}) {
            return autoplay === 'inview';
        }

    },

    connected() {

        if (this.inView && !hasAttr(this.$el, 'preload')) {
            this.$el.preload = 'none';
        }

    },

    ready() {

        this.player = new Player(this.$el);

        if (this.automute) {
            this.player.mute();
        }

    },

    update: [

        {

            read(_, {type}) {

                return !this.player || (type === 'scroll' || type === 'resize') && !this.inView
                    ? false
                    : {
                        visible: isVisible(this.$el) && css(this.$el, 'visibility') !== 'hidden',
                        inView: this.inView && isInView(this.$el)
                    };
            },

            write({visible, inView}) {

                if (!visible || this.inView && !inView) {
                    this.player.pause();
                } else if (this.autoplay === true || this.inView && inView) {
                    this.player.play();
                }

            },

            events: ['load', 'resize', 'scroll']

        }

    ]

};

animate.script:
import {addClass, append, assign, css, height, includes, index, isVisible, noop, position, Promise, removeClass, toFloat, toNodes, Transition} from 'uikit-util';

const targetClass = 'uk-animation-target';

export default {

    props: {
        animation: Number
    },

    defaults: {
        animation: 150
    },

    computed: {

        target() {
            return this.$el;
        }

    },

    methods: {

        animate(action) {

            addStyle();

            let children = toNodes(this.target.children);
            let propsFrom = children.map(el => getProps(el, true));

            const oldHeight = height(this.target);
            const oldScrollY = window.scrollY;

            action();

            Transition.cancel(this.target);
            children.forEach(Transition.cancel);

            reset(this.target);
            this.$update(this.target);

            return new Promise(resolve => {
                cancelAnimationFrame(this._raf);
                this._raf = requestAnimationFrame(() => {

                    const newHeight = height(this.target);

                    children = children.concat(toNodes(this.target.children).filter(el => !includes(children, el)));

                    const propsTo = children.map((el, i) =>
                        el.parentNode && i in propsFrom
                            ? propsFrom[i]
                            ? isVisible(el)
                                ? assign({
                                    width: el.offsetWidth,
                                    height: el.offsetHeight,
                                }, getPositionWithMargin(el))
                                : {opacity: 0}
                            : {opacity: isVisible(el) ? 1 : 0}
                            : false
                    );

                    propsFrom = propsTo.map((props, i) => {
                        const from = children[i].parentNode === this.target
                            ? propsFrom[i] || getProps(children[i])
                            : false;

                        if (from) {
                            if (!props) {
                                delete from.opacity;
                            } else if (!('opacity' in props)) {
                                const {opacity} = from;

                                if (opacity % 1) {
                                    props.opacity = 1;
                                } else {
                                    delete from.opacity;
                                }
                            }
                        }

                        return from;
                    });

                    addClass(this.target, targetClass);
                    children.forEach((el, i) => propsFrom[i] && css(el, propsFrom[i]));
                    css(this.target, 'height', oldHeight);
                    window.scroll(window.scrollX, oldScrollY);

                    Promise.all(children.map((el, i) =>
                        propsFrom[i] && propsTo[i]
                            ? Transition.start(el, propsTo[i], this.animation, 'ease')
                            : Promise.resolve()
                    ).concat(Transition.start(this.target, {height: newHeight}, this.animation, 'ease'))).then(() => {
                        children.forEach((el, i) => css(el, {display: propsTo[i].opacity === 0 ? 'none' : '', zIndex: ''}));
                        reset(this.target);
                        this.$update(this.target);
                        resolve();
                    }, noop);

                });
            });
        }
    }
};

function getProps(el, opacity) {

