    ],

    events: [

        {

            name: 'click',

            delegate() {
                return this.navItemSelector;
            },

            handler(e) {
                e.preventDefault();
                e.current.blur();
                this.show(data(e.current, this.attrItem));
            }

        },

        {

            name: 'itemshow',
            handler: 'updateNav'

        }

